mapper.select:
/* AccountRepository - selectAccountListCount */ SELECT count(*) as cnt FROM tb_account ac
ac.account_id LIKE CONCAT('%', #{searchText}, '%')
ac.name LIKE CONCAT('%', #{searchText}, '%')

/* AccountRepository - selectAccountList */ SELECT ac.account_idx, ac.account_id, ac.password, ac.name, ac.group_id, ac.status, ac.grade, ac.create_datetime, ac.update_datetime, (SELECT grp.group_name FROM tb_group grp WHERE ac.group_id = grp.group_id) as groupName FROM tb_account ac
ac.account_id LIKE CONCAT('%', #{searchText}, '%')
ac.name LIKE CONCAT('%', #{searchText}, '%')
LIMIT #{startIndex}, #{pageSize}

/* AccountRepository - selectAccountListByGroupIdCount */
SELECT 
	count(*) as cnt
FROM
	tb_account ac

	AND ac.group_id = #{groupId}

/* AccountRepository - selectAccountByAccountId */
SELECT 
	ac.account_idx, ac.account_id, ac.name, ac.password, ac.status
FROM
	tb_account ac

	ac.account_id = #{accountId}

/* AccountRepository - selectAccountByAccountId */
SELECT 
	ac.account_idx, ac.account_id, ac.name, ac.password, ac.status
FROM
	tb_account ac

	ac.account_idx = #{accountIdx}

mapper.insert:
/* AccountRepository insertAccount */
INSERT INTO tb_account(
	account_id, name, password, group_id 
) 
VALUES (
	#{accountId}, #{name}, #{password}, #{groupId}
)

mapper.update:
/* AccountRepository - updateAccounts */
UPDATE tb_account
SET
	group_id = #{groupId},
	update_id = #{updateId},
	update_datetime = #{updateDatetime}

	account_idx IN (
		
			#{item}
		
	)
